  formatDoc(sCmd, sValue) {
	  console.log("cmd:" + sCmd );
	  //console.log("cmd:" + sCmd + " " + sValue);
   if (this.validateMode()) { document.execCommand(sCmd, false, sValue); this.oDoc.focus(); }
   //if (this.validateMode()) { this.execCommand(sCmd, false, null); this.oDoc.focus(); }
  }
 
  validateMode() {
   //if (!document.compForm.switchMode.checked) { return true ; }
   if (this.shadowRoot.querySelector('#switchMode').checked)  { return true; }
   alert("Uncheck \"Show HTML\".");
   this.oDoc.focus();
   return false;
  }
 
  setDocMode(bToSource) {
   var oContent;
   if (bToSource) {
     oContent = document.createTextNode(oDoc.innerHTML);
     this.oDoc.innerHTML = "";
     var oPre = document.createElement("pre");
     this.oDoc.contentEditable = false;
     oPre.id = "sourceText";
     oPre.contentEditable = true;
     oPre.appendChild(oContent);
     this.oDoc.appendChild(oPre);
     document.execCommand("defaultParagraphSeparator", false, "div");
   } else {
     if (document.all) {
       this.oDoc.innerHTML = oDoc.innerText;
     } else {
       oContent = document.createRange();
       oContent.selectNodeContents(oDoc.firstChild);
       this.oDoc.innerHTML = oContent.toString();
     }
     this.oDoc.contentEditable = true;
   }
  this. oDoc.focus();
  }
 
  printDoc() {
   if (!validateMode()) { return; }
   var oPrntWin = window.open("","_blank","width=450,height=470,left=400,top=100,menubar=yes,toolbar=no,location=no,scrollbars=yes");
   oPrntWin.document.open();
   oPrntWin.document.write("<!doctype html><html><head><title>Print<\/title><\/head><body onload=\"print();\">" +this. oDoc.innerHTML + "<\/body><\/html>");
   oPrntWin.document.close();
  }

